import { Component } from '@angular/core';

@Component({
    selector: 'app-heroe',
    templateUrl: 'heroe.component.html'
})

export class HeroeComponent{

    nombre = "Ironman";
    edad = 45;


    get nombreCapitalizado(){
        return this.nombre.toUpperCase();
    }

    obtenerNombre(){
        return `${ this.nombre} - ${this.edad}`;
        // return this.nombre + " - " + this.edad;
    }

    cambiarNombre(){
        this.nombre = "SpiderMan";
    }

    cambiarEdad(){
        this.edad = 33;
    }
}
