import {Component} from '@angular/core';

@Component({
    selector: 'app-contador',
    template: `
        <h1>{{titulo}}</h1>
        <h3>La base es: <b>{{base}}</b></h3>
        <button (click)="acumular( base )"> + {{base}}</button>
        <span>{{numero}}</span>
        <button (click)="acumular(-base)"> - {{base}}</button>
        `
})
export class ContadorComponent{
    titulo = 'Contador Aoo';
    numero: number = 10;
    base: number = 5;
  
    sumar() {
      this.numero +=1;
    }
  
    restar() {
      this.numero -=1;
    }
  
    acumular( valor: number ) {
      this.numero += valor;
    }
}
