import { Component } from '@angular/core';


interface Personaje {
  nombre: String;
  poder: number;
}

@Component({
  selector: 'app-main-page',
  templateUrl: './main-page.component.html',
  styleUrls: ['./main-page.component.css']
})
export class MainPageComponent {

  nuevo: Personaje = {
    nombre : "El Nano",
    poder: 33
  }

  cambiarNombre( event: any){
    console.log(event)
  }

  agregar(){
  if (this.nuevo.nombre.trim().length === 0){
    return;
  }

    console.log(this.nuevo);
  }


}
<h1>Dragon Ball</h1>
<hr>

<div class="row">
    <div class="col">
        <h3>Personajes</h3>
        <hr>
        <ul>
            <li>Krillin - 700</li>
            <li>Goku - 15,000</li>
            <li>Vegeta - 8,500</li>
        </ul>
    </div>
    <div class="col">
        <h3>Agregar</h3>
        <hr>

        <form (ngSubmit)="agregar()">
            <input 
                type="text" 
                placeholder="Nombre" 
                [value]="nuevo.nombre"
                (input)="cambiarNombre($event)"
            />

            <input 
                type="number" 
                placeholder="Poder" 
                [value]="nuevo.poder"
            />

            <button type="submit">Agregar</button>

        </form>
    </div>
</div>
